import Item from "./item";
import eventBus from '@/utils/eventBus';
export default {
  components: {
    Item: Item
  },
  data: function data() {
    return {
      page: null,
      command: null,
      // 可拉伸侧边栏
      isActive: false,
      startX: 0,
      endX: 0,
      DEFAULT_ITEM_WIDTH: 200,
      DEFAULT_RESIZER_LEFT: 190,
      MAX_ITEM_WIDTH: 400,
      MIN_ITEM_WIDTH: 180
    };
  },
  computed: {
    itemWidth: {
      get: function get() {
        return this.$store.getters.itemWidth;
      },
      set: function set(val) {
        this.$store.dispatch('app/setItemPannelWidth', val);
      }
    }
  },
  created: function created() {
    this.bindEvent();
  },
  methods: {
    bindEvent: function bindEvent() {
      var _this = this;

      eventBus.$on('afterAddPage', function (page) {
        _this.page = page;
      });
    },
    onResizeMouseDown: function onResizeMouseDown(e) {
      var _this2 = this;

      this.isActive = true;
      this.startX = e.clientX;
      var itempannel = document.getElementById('itempannel');
      var resizer = document.getElementById('item-resizer');
      var preItemWidth = this.itemWidth;

      document.onmousemove = function (e) {
        _this2.onResizeMouseMove(e, preItemWidth, itempannel, resizer);
      };

      document.onmouseup = function (e) {
        _this2.isActive = false;
        var nowItempannelWidth = parseInt(itempannel.style.width.replace('px', ''));

        if (nowItempannelWidth !== _this2.itemWidth) {
          _this2.itemWidth = nowItempannelWidth;
        } // this.$store.dispatch(
        //   'app/setItemPannelWidth',
        //   parseInt(itempannel.style.width.replace('px', ''))
        // )

      };
    },
    onResizeMouseMove: function onResizeMouseMove(e, preItemWidth, itempannel, resizer) {
      if (e.witch === 0) {
        this.isActive = false;
      }

      if (this.isActive) {
        this.endX = e.clientX;
        var nowItempannelWidth = parseInt(itempannel.style.width.replace('px', ''));

        if (nowItempannelWidth <= this.MAX_ITEM_WIDTH && nowItempannelWidth >= this.MIN_ITEM_WIDTH) {
          itempannel.style.width = preItemWidth - this.startX + this.endX + 'px';
          resizer.style.left = preItemWidth - this.startX + this.endX + 'px';
        }

        nowItempannelWidth = parseInt(itempannel.style.width.replace('px', ''));
        var nowResizerLeft = parseInt(resizer.style.left.replace('px', ''));

        if (nowItempannelWidth > this.MAX_ITEM_WIDTH) {
          itempannel.style.width = "".concat(this.MAX_ITEM_WIDTH, "px");
          resizer.style.left = "".concat(this.MAX_ITEM_WIDTH - 10, "px");
        } else if (nowItempannelWidth < this.MIN_ITEM_WIDTH || nowResizerLeft < this.MIN_ITEM_WIDTH) {
          itempannel.style.width = "".concat(this.MIN_ITEM_WIDTH, "px");
          resizer.style.left = "".concat(this.MIN_ITEM_WIDTH - 10, "px");
          resizer.style.left = "".concat(this.MIN_ITEM_WIDTH - 10, "px");
        }
      }
    }
  }
};
