<template>	<div class="main">		<div class="terminal-top">			<div class="a" v-if="terminalOpen === 0">				<div class="top-left">					<span>变量名</span>					<span>变量类型</span>					<span>变量值</span>				</div>			</div>			<div class="b" v-if="terminalOpen === 1">				<span>ln</span>				<span>代码</span>			</div>			<div class="c" v-if="terminalOpen === 2"><span>实时数据(每分钟采样一次) 硬盘 0GB/100GB</span></div>			<div class="top-right" @click="offTerminal">×</div>		</div>		<div class="terminal-body">			<div v-if="terminalOpen === 0" class="a"></div>			<div v-if="terminalOpen === 1" class="b"></div>			<div v-if="terminalOpen === 2" class="c">				<el-row>					<el-col :span="4">						<div class="body-left">							<div class="a">								<p>CPU</p>								<p>5%</p>							</div>							<div class="a">								<p>内存</p>								<p>0.16GB/8GB</p>							</div>						</div>					</el-col>					<el-col :span="20"><div class="body-right">ddd</div></el-col>				</el-row>			</div>		</div>		<div class="terminal-foot">			<div class="foot-left">命令模式</div>			<div class="foot-right">				<div class="actions" :style="{ backgroundColor: terminalOpen === 0 ? `#BFD1E2` : `` }" @click="terminalTrans(0)">变量监控</div>				<div class="actions" :style="{ backgroundColor: terminalOpen === 1 ? `#BFD1E2` : `` }" @click="terminalTrans(1)">运行历史</div>				<div class="actions" :style="{ backgroundColor: terminalOpen === 2 ? `#BFD1E2` : `` }" @click="terminalTrans(2)">性能监控</div>			</div>		</div>	</div></template><script>	import { mapState } from 'vuex'import { mapGetters } from 'vuex';export default {	data() {		return {			terminalOpen: 0		};	},	methods: {		terminalTrans(i) {			this.terminalOpen = i;		},				offTerminal() {			this.$store.commit('app/SET_TERMINALDISPLAY', 'none')		}	},	computed: {		...mapGetters(['terminalDisplay'])	}};</script><style lang="scss" scoped>$hei: 270px;.main {	z-index: 15;	width: 100%;	height: $hei;	position: absolute;	bottom: 0;